import React, { useState } from "react";
import axios from "axios";
import './Input.css'

const Input=()=>{
    const [inputText,setInputText]=useState('')
    const [botMessage,setBotMessage]=useState(true)

    const toggleMessage=async (e)=>{
      setBotMessage(!botMessage)
      e.preventDefault()
      try{
        const endPoint=''
        const inputQuery={inputText}
        console.log(inputQuery)
        input_bar=document.querySelector('#input_text').innerHTML=''
        const response=await axios.post(endPoint,inputQuery)
        console.log(response)
      }
      catch(error){
        console.log('Error: ',error)
      };
    }
    const inputHandler=(e)=>{
        // console.log(e.target.value)
        setInputText(e.target.value)
    }
    return(
        <div className="btm">
            <div className="input">
            <input
                type="text"
                id="input_text"
                placeholder="Enter your Message"
                onChange={inputHandler}/>
            </div>
            <div className="btn">
                <button className="send-btn" onClick={toggleMessage}>
                    <i className="fa fa-paper-plane"></i>Send
                </button>
            </div>
        </div>
        
    )
}

export default Input;
