import React from "react";
import user from '../../image/user.png'
import './styleHuman.css'

const HumanMessage=({key,message,timestamp})=>{
    return(
        <div className="message-container human" key={key}>
            <div> <img src={user} alt='User' className="message-avatar"/></div>
            <div className='human-message'>{message}</div>
            <div className="message-time">{formatTime(timestamp)}</div>
        </div>
    )
}
export default HumanMessage;
