import React from "react";
// import { useState,useRef } from "react";
import './Messages.css'
import bot_icon from '../../image/bot_icon.jpg'
import user from '../../image/user.png'

const Messages=({messages})=>{
    const formatTime=(timestamp)=>{
        const date=new Date(timestamp);
        const hours=date.getHours();
        const minutes=date.getMinutes();
        return `${hours}:${minutes<10?'0':''}${minutes}`
    }
    return(
        <div className="messages">
            <div>
                <div><img src={message.sender==='bot'?bot_icon:user} className="message-avatar"/></div>
                <div className="message bot-message">Hi I am chitti. How can I help you?</div>
            </div>
           {messages.map((message,index)=>(
            <div key={index} className={`message ${message.sender==="bot"?"bot-message":"human-message"}`}>
                <div><img src={message.sender==='bot'?bot_icon:user} className="message-avatar"/></div>
                <div className="message-text">{message.text}</div>
                <div className="message-time">{formatTime(message.timestamp)}</div>
            </div>
           ))}
        </div>
    )

}

export default Messages
