import React from "react";
import bot_icon from '../../image/bot_icon.jpg'
import './styleBot.css'

const Botmessage=({key,message,timestamp})=>{
    return(
        <div className="message-container bot" key={key}>
            <div> <img src={bot_icon} alt='Bot' className="message-avatar"/></div>
            <div className='bot-message'>{message}</div>
            <div className="message-time">{formatTime(timestamp)}</div>
        </div>
    )
}
export default Botmessage;
