import './App.css';
import Header from './components/Header/Header';
import Messages from './components/Messages/Messages';
import Input from './components/Input/Input';
import Chatbotbox from './components/chatbox/Chatbotbox';
import { useEffect, useRef, useState } from 'react';
import axios from 'axios';

function App() {

    const [isChatboxOpen,setIsChatboxOpen]=useState(false)
    const [status,setSatus]=useState('Not Active')
    const [messages,setMessages]=useState([])
    const inputRef=useRef();


    const toggleChatbox=()=>{
      setIsChatboxOpen(!isChatboxOpen)
    }
    useEffect(()=>{
      if(isChatboxOpen){
        setSatus('Active');
      }
      else{
        setSatus('Not Active')
      }
    },[isChatboxOpen]);


    const handleSend=async()=>{
      const userMessage=inputRef.current.value;
      if(!userMessage.trim()) return;
      const newMessage=[...messages,{sender:'human',text:userMessage,time}];
      setMessages(newMessage)
      inputRef.current.value="";
      console.log(messages)
      try{
        const endPoint='http://localhost:8000/api/runQuery'
        const response=await axios.post(endPoint,{message:userMessage});
        console.log(response)
        const botMessage={
          text:response.data.name,
          sender:'bot',
          timestamp:new Date().toISOString()
        }
        setMessages(prevMessages=>[...prevMessages,botMessage])
      }
      catch(error){
        console.log('error sending message: ',error)
      }
    }

  return (
    <div className="App">
        {/* {isChatboxOpen && ( */}
          <div className={`wrapper ${isChatboxOpen?'open':'closed'}`}>
          <div className="content">
            <Header name="ChatBot" status={status} closeChat={toggleChatbox}/>
          </div>
          <div className="main">
            <div className="main_content">
              <Messages messages={messages}/>
            </div>
            <div className="bottom"> 
              <Input inputRef={inputRef} handleSend={handleSend}/>
            </div>
          </div>
          </div>
        {/* )} */}
      <div className="chatbot_icon">
        <Chatbotbox isOpen={isChatboxOpen} toggleChatbox={toggleChatbox}/>
      </div>
    </div>
  );
}

export default App;
