import React from "react";
// import { useState,useRef } from "react";
import './Messages.css'
import bot_icon from '../../image/bot_icon.'
import user from '../../image/user.png'

const Messages=({messages})=>{
    return(
        <div className="messages">
            <div className="message bot-message">Hi I am chitti. How can I help you?</div>
           {messages.map((message,index)=>(
            <div key={index} className={`message ${message.sender==="bot"?"bot-message":"human-message"}`}>{message.text}</div>
           ))}
        </div>
    )

}

export default Messages
