  //   }
  //   else
  //   {
  //     status.innerHTML="Not Active";
  //     status.style.color="red";
  //   }
  // }
    const [isChatboxOpen,setIsChatboxOpen]=useState(false)
    const toggleChatbox=()=>{
      setIsChatboxOpen
    }
    useEffect(()=>{
      const status=document.querySelector(".status");
      if(isChatboxOpen){
        status.innerHTML="Active";
        status.computedStyleMap.color="green";
      }
      else
      {
        status.innerHTML="Not Active";
        status.computedStyleMap.color="red";
      }
    },[isChatboxOpen]);
  return (
    <div className="App">
      <div className="wrapper">
        <div className="content">
          <Header name="ChatBot" status="Active" />
        </div>
        <div className="main">
          <div className="main_content">
            <Messages />
          </div>
          <div className="bottom"> 
            <Input />
          </div>
        </div>
      </div>
      <div className="chatbot_icon">
        <Chatbotbox />
      </div>
    </div>
  );
}

export default App;
