import React from "react";
import bot_icon from '../../image/bot_icon.jpg'
import './styleBot.css'

const Botmessage=({messages,formatTime})=>{
    return(
        <div className="message-container bot">
            <div> <img src={bot_icon} alt={messages.sender} className="message-avatar"/></div>
            <div className='bot-message'>{messages.text}</div>
            <div className="message-time">{formatTime(messages.timestamp)}</div>
        </div>
    )
}
export default Botmessage;
